$(function(){$(document).on("click",".product-view .product-add-bookmark .add-bookmark",function(){var d=$(this),o=d.data("product-id");$.post("/api/product_bookmarks/add",{productId:o},function(o){if($.handleAjaxResult(o),o.bookmarked){var a=d.closest(".product-add-bookmark").find(".bookmarked");d.addClass("hide"),a.removeClass("hide")}})})});